mapper.select:
SELECT AAA.* FROM(
SELECT COUNT(*) OVER() AS TOTAL_COUNT, AA.* FROM( SELECT ROW_NUMBER() OVER (ORDER BY ROLE_ID ASC) RNUM, MEMBER_ID, MEMBER_NICKNAME, MEMBER_POINT, DECODE(MEMBER_GENDER,1,'남자',0,'여자',null,'미기입') AS MEMBER_GENDER, DECODE(MEMBER_ADDRESS,null,'미기입',MEMBER_ADDRESS) AS MEMBER_ADDRESS, DECODE(MEMBER_MARRY,null,'미기입',MEMBER_MARRY) AS MEMBER_MARRY, DECODE(MEMBER_AREA,null,'미기입',MEMBER_AREA) AS MEMBER_AREA, DECODE(MEMBER_JOB,null,'미기입',MEMBER_JOB) AS MEMBER_JOB, DECODE(ROLE_ID,0,'회원',1,'관리자',2,'비회원',3,'가회원') AS ROLE_ID, MEMBER_BIRTH FROM MEMBER_LIST
) AA ) AAA WHERE AAA.RNUM BETWEEN #{START} AND #{END}
